mapper.select:
SELECT i.*, c.name as category_name FROM item i
LEFT JOIN category c ON i.category_no = c.category_no 
WHERE item_no = ${item_no}

mapper.update:
UPDATE category SET
				name = #{name}
WHERE category_no = #{category_no}

mapper.insert:
INSERT INTO category (
	name
) VALUES (
	#{name}
)

	SELECT LAST_INSERT_ID()
